(function(){
  const K="_tl",C="_lc",L=3;
  window._P=function(m){
    document.getElementById("_p")?.remove();
    document.body.insertAdjacentHTML("beforeend",
    "<div id=_p style='position:fixed;inset:0;background:rgba(0,0,0,.85);z-index:999999;display:flex;align-items:center;justify-content:center'>"+
    "<div style='background:#fff;padding:20px;width:300px;border-radius:6px;text-align:center'>"+
    "<h3>Aktivasi Lisensi</h3>"+
    (m?"<p style='color:red;font-size:13px'>"+m+"</p>":"")+
    "<input id=_i placeholder='Kode Lisensi' style='width:100%;padding:8px;margin:10px 0'>"+
    "<button id=_b style='padding:8px 16px;width:100%'>Aktifkan</button>"+
    "</div></div>");
    document.getElementById("_b").onclick=function(){
      var v=document.getElementById("_i").value.trim();
      if(!v)return;
      localStorage.setItem(K,v);
      location.reload();
    }
  };
  window._G=function(){return localStorage.getItem(K)};
  window._A=function(){
    var c=parseInt(localStorage.getItem(C)||"0")+1;
    localStorage.setItem(C,c);
    return c>=L;
  };
})();
